import React from "react";
import { useContext } from "react";
import { Outlet, NavLink } from "react-router-dom";
import TokenContext from "../../context/TokenContext.js";
import "./header.css";
function Header() {
  const token = localStorage.getItem("authToken");
  const { user } = useContext(TokenContext);
  console.log("user", user);
  const logout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login";
  };

  return (
    <div>
      <nav className="header">
        <div className="app-name">
          {/* <NavLink className="app-name-component" to="/">
            <h2 >The Daily Hub</h2>
          </NavLink> */}
        </div>
        <div className="app-component-btns">
          {token ? (
            <div className="component-container">
              <ul className="components-list">
                <li>
                  <NavLink className="component" to="/">
                    Home
                  </NavLink>
                </li>
                <li>
                  <NavLink className="component" to="/notes">
                    Notes
                  </NavLink>
                </li>
                <li>
                  <NavLink className="component" to="/expense">
                    Expense Tracker
                  </NavLink>
                </li>
                <li>
                  <NavLink className="component" to="/weather">
                    Weather
                  </NavLink>
                </li>
              </ul>
              <p className="welcome-txt">
                Welcome, <span className="username">{user.name}</span>
              </p>
              <button onClick={logout} className="component">
                Logout
              </button>
            </div>
          ) : (
            <ul className="flex justify-end gap-3 w-3/4 pr-6">
              <li>
                <NavLink className="login-btn" to="/login">
                  Login
                </NavLink>
              </li>
              <li>
                <NavLink className="register-btn" to="/register">
                  Register
                </NavLink>
              </li>
            </ul>
          )}
        </div>
      </nav>
      <Outlet />
    </div>
  );
}

export default Header;
