import React, { useState } from "react";
import { useContext } from "react";
import { Outlet, NavLink } from "react-router-dom";
import TokenContext from "../../context/TokenContext.js";
import "./header.css";
function Header() {
  const token = localStorage.getItem("authToken");
  const { user } = useContext(TokenContext);
  const [translateValue, setTranslateValue] = useState(0);
  console.log("user", user);
  const logout = () => {
    localStorage.removeItem("authToken");
    window.location.href = "/login";
  };

  const handleItemClick = (index) => {
    // Calculate the translation value based on index and item width
    const newTranslateValue = `translateX(calc(${index} * (100% / ${items.length})))`;
    // Update state to trigger re-render and apply the translation
    setTranslateValue(newTranslateValue);
  };
  return (
    <div>
      <nav className="header">
        <div className="app-name">
          <NavLink className="app-name-component" to="/">
            The Daily Hub
          </NavLink>
        </div>
        <div className="app-component-btns">
          {token ? (
            <div className="component-container">
              <nav className="nav">
                <ul className="components-list">
                  <li>
                    <NavLink
                      className="component"
                      to="/"
                      onClick={() => handleItemClick(0)}
                    >
                      To Do
                    </NavLink>
                  </li>
                  <li>
                    <NavLink
                      className="component"
                      to="/notes"
                      onClick={() => handleItemClick(1)}
                    >
                      Notes
                    </NavLink>
                  </li>
                  <li>
                    <NavLink
                      className="component"
                      to="/expense"
                      onClick={() => handleItemClick(2)}
                    >
                      Expense Tracker
                    </NavLink>
                  </li>
                  <li>
                    <NavLink
                      className="component"
                      to="/weather"
                      onClick={() => handleItemClick(3)}
                    >
                      Weather
                    </NavLink>
                  </li>
                  {/* Navigation indicator */}
                  <div
                    className="indicator"
                    style={{ transform: translateValue }}
                  ></div>
                </ul>
              </nav>
              <div className="header-last">
                <p className="welcome-text">
                  Welcome, {token ? user.name : "Vikram"}
                </p>
                <button onClick={logout} className="logout">
                  Logout
                </button>
              </div>
            </div>
          ) : (
            <ul className="flex justify-end gap-3 w-3/4 pr-6">
              <li>
                <NavLink className="login-btn" to="/login">
                  Login
                </NavLink>
              </li>
              <li>
                <NavLink className="register-btn" to="/register">
                  Register
                </NavLink>
              </li>
            </ul>
          )}
        </div>
      </nav>
      <Outlet />
    </div>
  );
}

export default Header;
